$(document).ready(function () {
$(function () {
  $('.reviews__items').slick({
    dots: true,
    slidesToShow: 3,
    arrows: true,
    slidesToScroll: 1,
    prevArrow: '<button class="slick-arrow slick-sale__prev"></button>',
    nextArrow: '<button class="slick-arrow slick-sale__next"></button>',
    variableWidth: true,
    responsive: [{
      breakpoint: 1250,
      settings: {
        slidesToShow: 2,
        centerMode: true,
        infinite: true,
      }
    }, ]

  });

  const menuBtn = document.querySelector('.burger');
  const menuHover = document.querySelector('.burger');
  let menuOpen = false;
  let menuHoverOn = false;

  menuBtn.addEventListener('click', () => {
    if (!menuOpen) {
      menuBtn.classList.add('burger_open');
      menuOpen = true;
    } else {
      menuBtn.classList.remove('burger_open');
      menuOpen = false;
    }
  });

  menuHover.addEventListener('mouseenter', () => {
    if (!menuHoverOn) {
      menuHover.classList.add('burger_hover');
      menuHoverOn = true;
    } else {
      menuHover.classList.remove('burger_hover');
      menuHoverOn = false;
    }
  });

  $(".burger").click(function () {
        $('.header__menu').toggleClass("active_menu");
  });

});

   $(".menu").on("click","a", function (event) {
        event.preventDefault();
        var id  = $(this).attr('href'),
            top = $(id).offset().top - 90;
        $('body,html').animate({scrollTop: top}, 1500);
        $(".burger").click();
    });
  
     $(".menu2").on("click","a", function (event) {
        event.preventDefault();
        var id  = $(this).attr('href'),
            top = $(id).offset().top - 90;
        $('body,html').animate({scrollTop: top}, 1500);
    });
    
  
});
